mapper.select:
SELECT COUNT(*)
FROM SCH_INCU.PROJECT
WHERE 1=1
AND DEL_YN = 'N'
AND EVA_CD != '00'

    AND PROJECT_NAME LIKE '%' || #{projectName} || '%'


    AND EVA_CD = #{evaCd}


    AND TO_CHAR(FRST_RGSR_DTL_DTTM, 'yyyy-mm-dd') >= #{startDate}
    AND TO_CHAR(FRST_RGSR_DTL_DTTM, 'yyyy-mm-dd') <= #{endDate}

SELECT * FROM ( SELECT ROW_NUMBER() OVER() AS RNUM , * FROM ( SELECT PROJECT_ID , PROJECT_NAME , EVA_CD , (SELECT CODE_NAME FROM SCH_INCU.CODE WHERE UPPER_CODE_NAME = 'EVALUATION' AND CODE_VAL = EVA_CD) AS EVA_CD_NM , SCORE_FIN , TO_CHAR(EVA_START_DT, 'yyyy-mm-dd') AS EVA_START_DT , TO_CHAR(EVA_END_DT, 'yyyy-mm-dd') AS EVA_END_DT , DEL_YN , FRST_RGSR_ID , TO_CHAR(FRST_RGSR_DTL_DTTM, 'yyyy-mm-dd') AS FRST_RGSR_DTL_DTTM , LAST_CHNG_ID , TO_CHAR(LAST_CHNG_DTL_DTTM, 'yyyy-mm-dd') AS LAST_CHNF_DTL_DTTM , USER_GROUP_ID , EVA_GROUP_ID , PROJECT_DETAIL , URL , CASE WHEN EVA_GROUP_ID IS NULL THEN 'N' ELSE 'Y' END AS EVA_FORM_YN FROM SCH_INCU.PROJECT WHERE 1=1 AND DEL_YN = 'N' AND EVA_CD != '00'
AND PROJECT_NAME LIKE '%' || #{projectName} || '%'
AND EVA_CD = #{evaCd}
AND TO_CHAR(FRST_RGSR_DTL_DTTM, 'yyyy-mm-dd') >= #{startDate} AND TO_CHAR(FRST_RGSR_DTL_DTTM, 'yyyy-mm-dd') <= #{endDate}
) T1 ) T2
WHERE RNUM > #{firstRecordIndex} AND RNUM <= #{lastRecordIndex}
