mapper.select:
SELECT A.PROJECT_ID AS projectId, A.PROJECT_NAME AS projectName, B.USER_NAME AS userName, A.EVA_START_DT AS evaStartDt, A.EVA_END_DT AS evaEndDt, C.CODE_NAME AS codeName, A.EVA_CD AS evaCd, A.SCORE_FIN AS scoreFin, A.DEL_YN AS delYn, A.FRST_RGSR_ID AS firstRgsrId, A.FRST_RGSR_DTL_DTTM AS frstRgsrDtlDttm, A.LAST_CHNG_ID AS lastChngId, A.LAST_CHNG_DTL_DTTM AS lastChngDtlDttm, A.USER_GROUP_ID AS userGroupId, A.EVA_GROUP_ID AS evaGroupId, A.PROJECT_DETAIL AS projectDetail, A.URL AS url FROM SCH_INCU.PROJECT A LEFT JOIN SCH_INCU.USER B ON A.LAST_CHNG_ID = B.USER_EMAIL LEFT JOIN SCH_INCU.CODE C ON A.EVA_CD = C.CODE_VAL AND C.UPPER_CODE_NAME = 'EVALUATION' WHERE A.LAST_CHNG_ID = #{userEmail} AND A.DEL_YN = 'N'
AND A.PROJECT_NAME LIKE CONCAT('%', #{searchProjectName}, '%')
AND DATE(A.EVA_START_DT) >= CAST(#{searchStartDate} AS DATE)
AND DATE(A.EVA_END_DT) <= CAST(#{searchEndDate} AS DATE)
AND A.EVA_CD = #{searchEvaCd}
ORDER BY A.FRST_RGSR_DTL_DTTM DESC LIMIT #{pageSize} OFFSET #{offset}
